document.addEventListener("click",e=>{const t=document.querySelector(".hamburger-menu");if(e.target.classList.contains("hamburger-menu")||e.target.classList.contains("hamburger-menu-item"))t.classList.toggle("is-active"),document.querySelector(".header__menu-list").classList.toggle("header__menu-list--hidden");else{if(e.target.classList.contains("menu-list__item")||e.target.classList.contains("menu-list"))return;t.classList.remove("is-active"),document.querySelector(".header__menu-list").classList.add("header__menu-list--hidden")}});